import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

const Login: React.FC = () => {
  const [cpf, setCpf] = useState("");
  const [senha, setSenha] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await fetch("http://localhost:3000a/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ cpf, senha }),
      });

      const data = await response.json();

      if (response.ok) {
        // Redireciona para a tela inicial se a autenticação for bem-sucedida
        navigate("/tela-inicial");
      } else {
        // Exibe mensagem de erro se a autenticação falhar
        setError(data.message);
      }
    } catch (error) {
      console.error("Você não possui uma conta no nosso sistema:", error);
      setError("Você não possui uma conta no nosso sistema");
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <div>
        <label htmlFor="cpf">CPF:</label>
        <input
          type="text"
          id="cpf"
          value={cpf}
          onChange={(e) => setCpf(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="senha">Senha:</label>
        <input
          type="password"
          id="senha"
          value={senha}
          onChange={(e) => setSenha(e.target.value)}
        />
      </div>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <button onClick={handleLogin}>Entrar</button>
      <p>
        Ainda não tem cadastro? <Link to="/cadastro">Crie uma conta aqui.</Link>
      </p>
    </div>
  );
};

export default Login;
