import React, { useState, useEffect } from "react";
import { Chart } from "react-google-charts";

interface FinancialEntry {
  id: number;
  type: "Receita" | "Despesa" | "Transferência";
  description: string;
  amount: number;
  date: string;
}

const FinancialManagement: React.FC = () => {
  const [financialData, setFinancialData] = useState<FinancialEntry[]>([]);
  const [currentBalance, setCurrentBalance] = useState(0);

  useEffect(() => {
    // Aqui você fará uma chamada à API para obter os dados financeiros
    // Por enquanto, usaremos dados de exemplo
    const sampleFinancialData: FinancialEntry[] = [
      { id: 1, type: "Receita", description: "Consulta", amount: 100, date: "2024-03-18" },
      { id: 2, type: "Despesa", description: "Medicamentos", amount: 50, date: "2024-03-17" },
      { id: 3, type: "Transferência", description: "Transferência bancária", amount: 30, date: "2024-03-16" },
      // Outros registros financeiros...
    ];
    setFinancialData(sampleFinancialData);
  }, []);

  // Calculando o saldo atual com base nos registros financeiros
  useEffect(() => {
    const newBalance = financialData.reduce((balance, item) => {
      if (item.type === "Receita" || item.type === "Transferência") {
        return balance + item.amount;
      } else {
        return balance - item.amount;
      }
    }, 0);
    setCurrentBalance(newBalance);
  }, [financialData]);

  // Função para adicionar lançamento financeiro
  const addFinancialEntry = () => {
    // Aqui você pode implementar a lógica para adicionar um novo lançamento financeiro
    // Por enquanto, vamos apenas adicionar um lançamento de exemplo
    const newFinancialEntry: FinancialEntry = {
      id: financialData.length + 1,
      type: "Despesa",
      description: "Nova Despesa",
      amount: 20,
      date: "2024-03-19",
    };
    setFinancialData([...financialData, newFinancialEntry]);
  };

  // Preparando os dados para o gráfico do Google Charts
  const chartData = financialData.map((item) => [item.description, item.amount]);

  return (
    <div>
      <h2>Gestão Financeira</h2>
      <button onClick={addFinancialEntry}>Adicionar Lançamento Financeiro</button>
      <h3>Saldo Atual: R$ {currentBalance.toFixed(2)}</h3>
      <Chart
        width={"500px"}
        height={"300px"}
        chartType="PieChart"
        loader={<div>Loading Chart</div>}
        data={[["Descrição", "Valor"], ...chartData]}
        options={{
          title: "Financeiro do Caixa",
        }}
        rootProps={{ "data-testid": "1" }}
      />
      <table>
        <thead>
          <tr>
            <th>Tipo</th>
            <th>Descrição</th>
            <th>Valor</th>
            <th>Data</th>
          </tr>
        </thead>
        <tbody>
          {financialData.map((item) => (
            <tr key={item.id}>
              <td>{item.type}</td>
              <td>{item.description}</td>
              <td>{item.amount}</td>
              <td>{item.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* Aqui você pode adicionar ferramentas adicionais de gestão financeira, como lançamento financeiro da agenda ou prontuário, cadastro de convênios, etc. */}
    </div>
  );
};

export default FinancialManagement;
