import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Cadastro: React.FC = () => {
  const [dadosCadastro, setDadosCadastro] = useState({
    nome: "",
    sobrenome: "",
    dataNascimento: "",
    sexo: "",
    telefone: "",
    cpf: "",
    email: "",
    senha: "",
    confirmacaoSenha: "",
  });

  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    setDadosCadastro((prevDados) => ({
      ...prevDados,
      [name]: name === "telefone" ? Number(value) : value,
    }));
  };

  const handleCadastro = async () => {
    // Adicionar a validação aqui
    try {
      const response = await fetch("http://localhost:3000/cadastro", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(dadosCadastro),
      });

      const data = await response.json();
      if (response.ok) {
        navigate("/login");
      } else {
        setError(data.message);
      }
    } catch (error) {
      console.error("Erro ao tentar realizar seu cadastro:", error);
      setError("Erro ao tentar realizar seu cadastro");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-cadastro-background">
      <div className="bg-white p-8 rounded-lg shadow-2xl max-w-lg w-full space-y-4">
        <h1 className="text-2xl font-bold text-center mb-6">Cadastro</h1>
        <form className="space-y-4" onSubmit={handleCadastro}>
          {Object.entries(dadosCadastro).map(([key, value]) => (
            <div key={key}>
              <label htmlFor={key} className="block text-sm font-medium text-gray-700">{key.charAt(0).toUpperCase() + key.slice(1).replace(/([A-Z])/g, ' $1').trim()}:</label>
              <input
                type={key === "senha" || key === "confirmacaoSenha" ? "password" : "text"}
                id={key}
                name={key}
                value={value}
                onChange={handleChange}
                className="mt-1 block w-full px-4 py-3 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
              />
            </div>
          ))}
          {error && <p className="text-red-500 text-sm">{error}</p>}
          <button type="submit" className="w-full py-3 bg-blue-600 hover:bg-blue-700 text-white font-bold rounded-md transition duration-200 ease-in-out">
            Cadastrar
          </button>
        </form>
        <p className="mt-6 text-center">
          Já possui uma conta? <Link to="/login" className="text-blue-500 hover:text-blue-700">Faça login aqui.</Link>
        </p>
      </div>
    </div>
  );
};

export default Cadastro;
