import React, { useState, useEffect } from "react";
import NavBar from "../../components/navBar";
import "../../css/main.css";

const main: React.FC = () => {
  // Estado para armazenar as próximas consultas
  const [upcomingAppointments, setUpcomingAppointments] = useState<any[]>([]);

  // Estado para armazenar os resultados de exames recentes
  const [recentResults, setRecentResults] = useState<any[]>([]);

  // Função para buscar as próximas consultas
  const fetchUpcomingAppointments = async () => {
    try {
      const response = await fetch('URL_DO_BACKEND/proximas-consultas');
      const data = await response.json();
      setUpcomingAppointments(data);
    } catch (error) {
      console.error('Erro ao buscar próximas consultas:', error);
    }
  };

  // Função para buscar os resultados de exames recentes
  const fetchRecentResults = async () => {
    try {
      const response = await fetch('URL_DO_BACKEND/resultados-exames-recentes');
      const data = await response.json();
      setRecentResults(data);
    } catch (error) {
      console.error('Erro ao buscar resultados de exames recentes:', error);
    }
  };

  // Chamada das funções de busca ao carregar o componente
  useEffect(() => {
    fetchUpcomingAppointments();
    fetchRecentResults();
  }, []);

  return (
    <div className="main-screen">
      <NavBar />
      <div className="main-content">
        <h2>Bem-vindo ao Sistema Clínico</h2>
        <div className="section-container">
          <section className="quick-links">
            {/* Links Rápidos */}
          </section>
          <section className="upcoming-appointments">
            <h3>Próximas Consultas</h3>
            <ul>
              {upcomingAppointments.map((appointment, index) => (
                <li key={index}>{appointment.doctor} - {appointment.date}, {appointment.time}</li>
              ))}
            </ul>
          </section>
          <section className="recent-results">
            <h3>Resultados de Exames Recentes</h3>
            <ul>
              {recentResults.map((result, index) => (
                <li key={index}>{result.type} - {result.date}</li>
              ))}
            </ul>
          </section>
          <section className="medical-history-summary">
            {/* Resumo do Histórico Médico */}
          </section>
        </div>
      </div>
    </div>
  );
};

export default main;
