import React, { useState, useEffect } from "react";

interface StockItem {
  id: number;
  name: string;
  quantity: number;
  lastUpdated: string;
  expirationDate: string;
}

const ClinicManagement: React.FC = () => {
  const [stockItems, setStockItems] = useState<StockItem[]>([]);
  const [expiredItemsCount, setExpiredItemsCount] = useState<number>(0); // Contador de itens vencidos

  useEffect(() => {
    // Aqui você fará uma chamada à API para obter os itens em estoque
    // Por enquanto, usaremos dados de exemplo
    const sampleStockItems: StockItem[] = [
      {
        id: 1,
        name: "Medicamento A",
        quantity: 100,
        lastUpdated: "2024-03-18",
        expirationDate: "2024-03-20",
      },
      {
        id: 2,
        name: "Medicamento B",
        quantity: 50,
        lastUpdated: "2024-03-17",
        expirationDate: "2024-03-25",
      },
      // Outros itens em estoque...
    ];

    // Atualizar o contador de itens vencidos
    const now = new Date();
    const expiredItems = sampleStockItems.filter(
      (item) => new Date(item.expirationDate) < now
    );
    setExpiredItemsCount(expiredItems.length);

    setStockItems(sampleStockItems);
  }, []);

  // Aqui você pode implementar funções para adicionar, remover ou atualizar itens em estoque

  // Função para calcular a quantidade total de itens em estoque
  const getTotalStock = () => {
    return stockItems.reduce((total, item) => total + item.quantity, 0);
  };

  return (
    <div>
      <h2>Gestão da Clínica</h2>
      <h3>Controle de Estoque</h3>
      <p>Total de Itens em Estoque: {getTotalStock()}</p>
      <p>Itens Vencidos: {expiredItemsCount}</p>
      <table>
        <thead>
          <tr>
            <th>Item</th>
            <th>Quantidade</th>
            <th>Última Atualização</th>
            <th>Data de Validade</th>
          </tr>
        </thead>
        <tbody>
          {stockItems.map((item) => (
            <tr
              key={item.id}
              className={
                new Date(item.expirationDate) < new Date() ? "expired" : ""
              }
            >
              <td>{item.name}</td>
              <td>{item.quantity}</td>
              <td>{item.lastUpdated}</td>
              <td>{item.expirationDate}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* Aqui você pode adicionar a ferramenta de histórico de movimentações */}
      <h3>Histórico de Movimentações</h3>
      {/* Implemente a ferramenta de histórico de movimentações aqui */}
    </div>
  );
};

export default ClinicManagement;
