import React, { useState, useEffect } from "react";
import axios from "axios";

// Interface para definir o tipo do objeto Paciente
interface Paciente {
  nome: string;
  sobrenome: string;
  dataNascimento: string;
  cpf: string;
  email: string;
  // Adicione outras propriedades conforme necessário
}

const PacienteInfo: React.FC<{ token: string }> = ({ token }) => {
  const [paciente, setPaciente] = useState<Paciente>({
    // Alteração aqui
    nome: "",
    sobrenome: "",
    dataNascimento: "",
    cpf: "",
    email: "",
  });
  const [editMode, setEditMode] = useState(false);
  const [email, setEmail] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:3000/paciente", {
          headers: {
            Authorization: token,
          },
        });
        setPaciente(response.data.paciente);
      } catch (error) {
        console.error("Erro ao obter informações do paciente:", error);
      }
    };

    fetchData();
  }, [token]);

  const handleEditClick = () => {
    setEditMode(true);
    setEmail(paciente.email || "");
  };

  const handleSaveClick = async () => {
    try {
      await axios.put(
        "http://localhost:3000/paciente",
        { email },
        {
          headers: {
            Authorization: token,
          },
        }
      );
      setEditMode(false);
    } catch (error) {
      console.error("Erro ao atualizar informações do paciente:", error);
    }
  };

  return (
    <div>
      <h2>Informações do Paciente</h2>
      <div>
        <strong>Nome:</strong> {paciente.nome || ""} {paciente.sobrenome || ""}
      </div>
      <div>
        <strong>Data de Nascimento:</strong> {paciente.dataNascimento || ""}
      </div>
      <div>
        <strong>CPF:</strong> {paciente.cpf || ""}
      </div>
      {editMode ? (
        <div>
          <label>Email:</label>
          <input
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button onClick={handleSaveClick}>Salvar</button>
        </div>
      ) : (
        <div>
          <strong>Email:</strong> {paciente.email || ""}
          <button onClick={handleEditClick}>Editar</button>
        </div>
      )}
    </div>
  );
};

export default PacienteInfo;
