import React, { useState, useEffect } from "react";
import { format } from "date-fns"
import { ptBR } from "date-fns/locale"

interface Patient {
  id: number;
  name: string;
  lastConsultation: string;
  dateOfBirth: string;
}

const PatientManagement: React.FC = () => {
  const [patients, setPatients] = useState<Patient[]>([]);
  const [searchTerm, setSearchTerm] = useState<string>("");
  const [searchResults, setSearchResults] = useState<Patient[]>([]);

  useEffect(() => {
    // Aqui você fará uma chamada à API para obter a lista de pacientes
    // Por enquanto, usaremos dados de exemplo
    const samplePatients: Patient[] = [
      {
        id: 1,
        name: "Maria Santos",
        lastConsultation: "2024-03-18",
        dateOfBirth: "1980-05-15",
      },
      {
        id: 2,
        name: "João Silva",
        lastConsultation: "2024-03-17",
        dateOfBirth: "1975-10-22",
      },
      // Outros pacientes...
    ];
    setPatients(samplePatients);
  }, []);

  useEffect(() => {
    // Implementar lógica de busca
    const results = patients.filter((patient) =>
      patient.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setSearchResults(results);
  }, [searchTerm, patients]);

  const handleSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

   const formatDate = (dateString: string): string => {
     return format(new Date(dateString), "dd/MM/yyyy", { locale: ptBR });
   };

  return (
    <div>
      <h2>Cadastro e Busca de Pacientes</h2>
      <input
        type="text"
        placeholder="Buscar paciente..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Última Consulta</th>
            <th>Data de Nascimento</th>
          </tr>
        </thead>
        <tbody>
          {searchResults.map((patient) => (
            <tr key={patient.id}>
              <td>{patient.name}</td>
              <td>{formatDate(patient.lastConsultation)}</td>
              <td>{formatDate(patient.dateOfBirth)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PatientManagement;
