import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

interface LoginProps {
  onLogin: (token: string) => void;
}

const Login: React.FC<LoginProps> = ({ onLogin }) => {
  const [cpf, setCpf] = useState("");
  const [senha, setSenha] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLoginClick = async () => {
    try {
      const response = await fetch("http://localhost:3000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ cpf, senha }),
      });

      const data = await response.json();
      onLogin(data.token);

      if (response.ok) {
        navigate("/tela-inicial"); // Redireciona para a tela inicial se a autenticação for bem-sucedida
      } else {
        setError(data.message); // Exibe mensagem de erro se a autenticação falhar
      }
    } catch (error) {
      console.error("Você não possui uma conta no nosso sistema:", error);
      setError("Você não possui uma conta no nosso sistema");
    }
  };

  return (
    <div
      className="login-container min-h-screen flex items-center justify-center bg-cover bg-[url('/path/to/your/background.jpg')]"
      style={{ backgroundImage: 'url("/path/to/your/background.jpg")' }}
    >
      <div className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full">
        <h1 className="text-xl font-bold text-center mb-6">Login</h1>
        <div className="mb-4">
          <label
            htmlFor="cpf"
            className="block text-sm font-medium text-gray-700"
          >
            CPF:
          </label>
          <input
            type="text"
            id="cpf"
            value={cpf}
            onChange={(e) => setCpf(e.target.value)}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>
        <div className="mb-6">
          <label
            htmlFor="senha"
            className="block text-sm font-medium text-gray-700"
          >
            Senha:
          </label>
          <input
            type="password"
            id="senha"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>
        {error && (
          <p className="error-message text-red-500 text-sm mb-4">{error}</p>
        )}
        <button
          className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded cursor-pointer transition-colors"
          onClick={handleLoginClick}
        >
          Entrar
        </button>
        <div className="mt-6 text-center">
          <p>
            Ainda não tem cadastro?{" "}
            <Link to="/cadastro" className="text-blue-500 hover:text-blue-700">
              Crie uma conta aqui.
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
